FROM admin
WHERE id = #{id}
  AND password = #{password}

/* com.mtf.sipa.mapper.SipaMngMapper.selectNoticeList */ SELECT seq , lang , notice_gbn , title , writer , write_date , final_regi_dttm , view_cnt FROM notice WHERE 1=1
AND title LIKE CONCAT('%',#{searchText},'%')
AND lang = #{condition}
ORDER BY notice_gbn desc, init_regi_dttm desc, title asc

