/* com.mtf.sipa.mapper.SipaMngMapper.selectPopupList */ SELECT seq , use_yn , title , published_date , expiration_date , width_pixel , left_pixel , top_pixel , align , writer , content , note , init_regi_pic , init_regi_dttm , final_regi_pic , final_regi_dttm FROM popup WHERE 1=1
AND title LIKE CONCAT('%',#{searchText},'%')
AND ( title LIKE CONCAT('%',#{searchText},'%') OR content LIKE CONCAT('%',#{searchText},'%') )
AND use_yn = #{useYn}
ORDER BY published_date DESC, expiration_date DESC

/* com.mtf.sipa.mapper.SipaMngMapper.getActivePopupCount */
SELECT COUNT(*)
FROM popup
WHERE 1=1
  AND use_yn = #{useYn}
  AND (published_date <= #{today}
    AND expiration_date >= #{today})

