/* com.mtf.sipa.mapper.SipaMngMapper.selectNoticeList */ SELECT seq , lang , notice_gbn , title , writer , write_date , final_regi_dttm , view_cnt FROM notice WHERE 1=1
AND title LIKE CONCAT('%',#{searchText},'%')
AND lang = #{condition}
ORDER BY notice_gbn desc, init_regi_dttm desc, title asc

mapper.update:
/* com.mtf.sipa.mapper.SipaMngMapper.updateNotice */
UPDATE notice
SET lang = #{lang}
, notice_gbn = #{noticeGbn}
, title = #{title}
, content = #{content}
, writer = #{writer}
, write_date = #{writeDate}

    , file_id_list = #{fileIdList}

, final_regi_dttm = DATE_FORMAT(NOW(), '%Y-%m-%d %T')
WHERE seq = #{seq}

